import React, { useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import { Formik } from 'formik';
import { View } from 'react-native';
import { MaterialIcons, Octicons, Ionicons, Fontisto } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';
import { EvilIcons } from '@expo/vector-icons';




import {
    StyledContainer,
    InnerContainer,
    PageLogo,
    PageTitle,
    SubTitle,
    StyledFormArea,
    LeftIcon,
    RightIcon,
    StyledButton,
    StyledInputLabel,
    StyledTextInput,

    ButtonText,
    Colors,
    MsgBox,
    Line,
    ExtraView,
    ExtraText,
    TextLink,
    TextLinkContent,
    WelcomeContainer,
    WelcomeImage,
    Avatar


} from './../components/styles';
const Welcome = () => {
    const [hidePassword, setHidePassword] = useState(true);

    return (
        <>
            <StatusBar style="light" />
            <InnerContainer>
                <WelcomeImage resizeMode="cover" source={require('./../assets/img/img9.png')} />

                <WelcomeContainer>
                    <PageTitle welcome={true}>Bienvenue </PageTitle>
                    <SubTitle welcome={true}> olga Simpson</SubTitle>
                    <SubTitle welcome={true}> olgaSimpson@gmail.com</SubTitle>

                    <StyledFormArea>
                        <Avatar resizeMode="cover" source={require('./../assets/img/img9.png')} />

                        <Line />
                        <StyledButton onPress={() => { }}>
                            <ButtonText>
                                Logout
                            </ButtonText>

                        </StyledButton>

                    </StyledFormArea>



                </WelcomeContainer>
            </InnerContainer>
        </>


    );
};

export default Welcome;
